mapper.select:

from (select * from house where house_name like '%'|| #{houseName} ||'%') house , 
	(select house_no, min(room.n_week_price) as price from room group by house_no) room
where house.no = room.house_no

from ( select * from house
addr like '${country}%'
and addr like '%${state}%'
) house , ( select room.house_no, min(room.n_week_price) as price from room LEFT OUTER JOIN (
select room_no from reservation where res_start <= #{resDate} and #{resDate} < res_end
) disable on room.no != disable.room_no group by house_no ) available where house.no = available.house_no
